import { createOAuthUserAuth, } from "@octokit/auth-oauth-user";
export async function getUserOctokitWithState(state, options) {
    return state.octokit.auth({
        type: "oauth-user",
        ...options,
        factory(options) {
            return new state.Octokit({
                authStrategy: createOAuthUserAuth,
                auth: options,
            });
        },
    });
}
